SELECT apellido1 AS "ALUMNOS: apellido1", apellido2, nombre FROM universidad.persona WHERE tipo LIKE 'alumno' ORDER BY apellido1, apellido2, nombre ASC;
SELECT nombre, apellido1, apellido2, telefono FROM universidad.persona WHERE tipo LIKE 'alumno' AND telefono IS NULL ORDER BY apellido1, apellido2, nombre ASC;
SELECT apellido1, apellido2, nombre, fecha_nacimiento FROM universidad.persona WHERE tipo LIKE 'alumno' AND fecha_nacimiento LIKE '1999%';
SELECT nombre, apellido1, apellido2, telefono, nif FROM universidad.persona WHERE tipo LIKE 'profesor' AND telefono IS NULL AND nif LIKE '%K';
SELECT asignatura.nombre AS asignatura, cuatrimestre, curso, grado.nombre AS grado FROM universidad.asignatura, universidad.grado WHERE cuatrimestre LIKE 1 AND curso LIKE 3 AND grado.id LIKE 7;
SELECT apellido1 AS "PROFESOR: apellido1", apellido2, persona.nombre, departamento.nombre AS departamento FROM universidad.persona, universidad.departamento WHERE tipo LIKE 'profesor' ORDER BY apellido1, persona.nombre ASC;
SELECT asignatura.nombre AS asignatura, anyo_inicio, anyo_fin FROM universidad.asignatura, universidad.curso_escolar, universidad.persona WHERE nif LIKE '26902806M' ORDER BY asignatura.nombre, anyo_inicio;
SELECT DISTINCT departamento.nombre AS departamento, grado.nombre AS grado FROM universidad.departamento, universidad.persona, universidad.asignatura, universidad.grado WHERE grado.nombre LIKE 'Grado en Ingeniería Informática (Plan 2015)';
SELECT apellido1 AS "ALUMNO: apellido1", apellido2, persona.nombre, asignatura.nombre AS asignatura, anyo_inicio, anyo_fin FROM universidad.persona, universidad.asignatura, universidad.curso_escolar WHERE anyo_inicio LIKE '2018' AND anyo_fin LIKE '2019' ORDER BY apellido1 ASC;
SELECT departamento.nombre AS departamento, apellido1, apellido2, persona.nombre FROM universidad.persona LEFT JOIN universidad.departamento ON universidad.persona.id = universidad.departamento.id WHERE tipo LIKE 'profesor' ORDER BY departamento.nombre, apellido1, apellido2, persona.nombre;
SELECT departamento.nombre AS departamento, apellido1 AS "PROFESORES SIN DEPARTAMENTO: apellido1", apellido2, persona.nombre FROM universidad.persona LEFT JOIN universidad.departamento ON universidad.persona.id = universidad.departamento.id WHERE tipo LIKE 'profesor' AND departamento.nombre IS NULL ORDER BY apellido1, apellido2, persona.nombre;
SELECT departamento.nombre AS departamento, id_profesor FROM universidad.departamento LEFT JOIN universidad.profesor ON universidad.departamento.id = universidad.profesor.id_profesor WHERE id_profesor IS NULL ORDER BY departamento.nombre;
SELECT DISTINCTROW apellido1, apellido2, persona.nombre, asignatura.nombre AS asignatura FROM universidad.persona, universidad.profesor LEFT JOIN universidad.asignatura ON universidad.profesor.id_profesor = universidad.asignatura.id_profesor WHERE asignatura.id_profesor IS NULL AND persona.tipo = 'profesor' ORDER BY apellido1, apellido2, persona.nombre;
SELECT asignatura.nombre AS asignatura, asignatura.id_profesor FROM universidad.profesor RIGHT JOIN universidad.asignatura ON universidad.profesor.id_profesor = universidad.asignatura.id_profesor WHERE asignatura.id_profesor IS NULL ORDER BY asignatura.nombre;
SELECT departamento.id AS id_depto, departamento.nombre AS departamento, alumno_se_matricula_asignatura.id_asignatura, COUNT(alumno_se_matricula_asignatura.id_curso_escolar) FROM universidad.departamento, universidad.asignatura LEFT JOIN universidad.alumno_se_matricula_asignatura on universidad.asignatura.id=universidad.alumno_se_matricula_asignatura.id_asignatura WHERE alumno_se_matricula_asignatura.id_curso_escolar IS NULL;
SELECT COUNT(*) AS "NUMERO TOTAL ALUMNOS" FROM universidad.persona WHERE tipo LIKE 'alumno';
SELECT COUNT(*) AS "NUMERO ALUMNOS NACIDOS EN 1999" FROM universidad.persona WHERE tipo LIKE 'alumno' AND fecha_nacimiento LIKE '1999%';
SELECT departamento.nombre AS departamento, apellido1 AS profesor, apellido2 AS ' ', persona.nombre AS ' ', COUNT(*) AS "NUM. PROFESORES DPTO." FROM universidad.profesor, universidad.departamento, universidad.persona WHERE profesor.id_profesor=persona.id AND profesor.id_departamento=departamento.id GROUP BY departamento.nombre ORDER BY COUNT(*) DESC, departamento.nombre ASC;
SELECT departamento.nombre AS departamento, COUNT(profesor.id_profesor) AS "NUM. PROFESORES" FROM universidad.departamento LEFT JOIN universidad.profesor ON universidad.departamento.id = universidad.profesor.id_departamento GROUP BY departamento.nombre ORDER BY departamento.nombre;
SELECT grado.nombre AS grado, COUNT(asignatura.nombre) AS "NUM. ASIGNATURAS" FROM universidad.grado LEFT JOIN universidad.asignatura ON universidad.grado.id = universidad.asignatura.id_grado GROUP BY grado.nombre ORDER BY COUNT(asignatura.nombre) DESC;
SELECT grado.nombre AS grado, COUNT(asignatura.nombre) AS "NUM. ASIGNATURAS" FROM universidad.grado LEFT JOIN universidad.asignatura ON universidad.grado.id = universidad.asignatura.id_grado GROUP BY grado.nombre HAVING COUNT(asignatura.nombre) > 40 ORDER BY COUNT(asignatura.nombre) DESC;
SELECT grado.nombre, asignatura.tipo AS tipo_asignatura, SUM(creditos) AS total_creditos FROM universidad.asignatura, universidad.grado GROUP BY grado.nombre, asignatura.tipo ORDER BY grado.nombre;
SELECT anyo_inicio, COUNT(alumno_se_matricula_asignatura.id_asignatura) AS nombre_alumnes_matriculats FROM universidad.alumno_se_matricula_asignatura, universidad.curso_escolar WHERE id_curso_escolar=curso_escolar.id GROUP BY id_curso_escolar ORDER BY anyo_inicio;
SELECT persona.id, persona.nombre, apellido1, apellido2, COUNT(asignatura.id_profesor) AS "NUM. ASIGNATURAS" FROM universidad.persona LEFT JOIN universidad.asignatura ON universidad.persona.id = universidad.asignatura.id_profesor WHERE persona.tipo LIKE 'profesor' GROUP BY persona.id ORDER BY COUNT(asignatura.id_profesor) DESC;
SELECT apellido1 AS "ALUMNO MÁS JOVEN: apellido1", apellido2, persona.nombre, fecha_nacimiento FROM universidad.persona WHERE tipo LIKE 'alumno' AND fecha_nacimiento = (SELECT MAX(fecha_nacimiento) FROM universidad.persona);
SELECT DISTINCTROW apellido1 AS "PROFESOR: apellido1", apellido2, persona.nombre, departamento.nombre AS departamento, asignatura.id_profesor AS asignatura FROM universidad.persona, universidad.departamento, universidad.profesor, universidad.asignatura WHERE persona.tipo LIKE 'profesor' AND profesor.id_profesor=departamento.id AND asignatura.id_profesor IS NULL;